(function ($) {
    'use strict';

    $.fn.enllax = function (opt) {

        var options = $.extend({
            ratio: 0,
            type: 'background', //foreground
            direction: 'vertical' //horizontal
        }, opt);

        var elem = $('#new');

        elem.each(enable);
    }
    function enable(index, item){
        var winHeight = $(window).height();
        var docHeight = $(document).height();
        var ratio;
        var type;
        var dir;
        var $this = $(item);
        var offset = $this.offset().top;
        var height = $this.outerHeight();
        var dataRat = $this.data('enllax-ratio');
        var dataType = $this.data('enllax-type');
        var dataDir = $this.data('enllax-direction');

        if (dataRat) {
            ratio = dataRat;
        }
        else {
            ratio = 0;
        }

        if (dataType) {
            type = dataType;
        }
        else {
            type = "frontground";
        }

        if (dataDir) {
            dir = dataDir;
        }
        else {
            dir = "vertical";
        }

        var bgY = Math.round(offset * ratio);
        var transform = Math.round((offset - (winHeight / 2) + height) * ratio);

        /*
         if (type == 'background') {
         if (dir == 'vertical') {
         $this.css({
         'background-position': 'center ' + -bgY + 'px'
         });
         }
         else if (dir == 'horizontal') {
         $this.css({
         'background-position': -bgY + 'px' + ' center'
         });
         }
         }
         */
        /*
         else if (type == 'foreground') {
         if (dir == 'vertical') {
         $this.css({
         '-webkit-transform': 'translateY(' + transform + 'px)',
         '-moz-transform': 'translateY(' + transform + 'px)',
         'transform': 'translateY(' + transform + 'px)'
         });
         }
         else if (dir == 'horizontal') {
         $this.css({
         '-webkit-transform': 'translateX(' + transform + 'px)',
         '-moz-transform': 'translateX(' + transform + 'px)',
         'transform': 'translateX(' + transform + 'px)'
         });
         }
         }
         */

        $(window).on('scroll', function () {
            var scrolling = $(this).scrollTop();

            bgY = Math.round((offset - scrolling) * ratio);
            transform = Math.round(((offset - (winHeight / 2) + height) - scrolling) * ratio);

            if (type == 'background') {
                if (dir == 'vertical') {
                    $this.css({
                        'background-position': 'center ' + -bgY + 'px'
                    });
                }
                else if (dir == 'horizontal') {
                    $this.css({
                        'background-position': -bgY + 'px' + ' center'
                    });
                }
            }
            else if ((type == 'foreground') && (scrolling < docHeight)) {
                transform = Math.min(0, Math.max(-460, transform));

                if (transform == 0) {
                    $this.addClass("bottom");
                } else if (transform == -460) {
                    $this.addClass("top");
                    if ($this.attr('id') != "old") {
                        $this.attr({'id': "old"});
                        $("#d3").append("<div id='new' class='card bottom' data-enllax-ratio='0.3' data-enllax-type='foreground'>" +
                            "hello" +
                            "</div>");
                        $('#new').enllax();
                    }
                }

                if (transform != 0 && transform != -460) {
                    if (transform > -360 && transform < -100) {
                        $this.attr("data-enllax-ratio", ".3")
                    } else {
                        $this.attr("data-enllax-ratio", ".1")
                    }
                    $this.removeClass("top");
                    $this.removeClass("bottom");
                }


                if (dir == 'vertical') {
                    $this.css({
                        '-webkit-transform': 'translateY(' + transform + 'px)',
                        '-moz-transform': 'translateY(' + transform + 'px)',
                        'transform': 'translateY(' + transform + 'px)'
                    });
                }
                else if (dir == 'horizontal') {
                    $this.css({
                        '-webkit-transform': 'translateX(' + transform + 'px)',
                        '-moz-transform': 'translateX(' + transform + 'px)',
                        'transform': 'translateX(' + transform + 'px)'
                    });
                }
            }
        });
    }
})(jQuery);
